import React from 'react';
import "./tab.css";

const tab =[
  {
    id:1,
    name: "Delivery",
    active_img:
    "https://b.zmtcdn.com/data/o2_assets/c0bb85d3a6347b2ec070a8db694588261616149578.png?output-format=webp",
    Backdrop: "FCEEC0",
    inactive_img:
    "https://b.zmtcdn.com/data/o2_assets/246bbd71fbba420d5996452be3024d351616150055.png",
  },
  {
    id:2,
    name: "Dining Out",
    active_img:
    "https://b.zmtcdn.com/data/o2_assets/30fa0a844f3ba82073e5f78c65c18b371616149662.png",
    Backdrop: "E5F3F3",
    inactive_img:
    "https://b.zmtcdn.com/data/o2_assets/78d25215ff4c1299578ed36eefd5f39d1616149985.png",
  },
  {
    id:3,
    name: "Nightlife",
    active_img:
    "https://b.zmtcdn.com/data/o2_assets/855687dc64a5e06d737dae45b7f6a13b1616149818.png",
    Backdrop: "EDf4FF",
    inactive_img:
    "https://b.zmtcdn.com/data/o2_assets/01040767e4943c398e38e3592bb1ba8a1616150142.png",
  }
];
const TabOption = ({ActiveTab,setActiveTab}) => {
  return (
    <div className="tab-options">
      <div className="max-width options-wrapper">
        {tab.map((tab)=>{
        return(
          <div
          onClick={()=> setActiveTab(tab.name)}
          className={`tab-item absolute-center cur-po
           ${ActiveTab===tab.name && "active-tab"}`}>
          <div 
        className='tab-image-container absolute-center'>
          style={{
            backgroundColor: `${
              ActiveTab===tab.name ? tab.Backdrop : ""
            }`,
          }}
          <img className='tab-image' alt={tab.name}
          src={
            ActiveTab === tab.name ? tab.active_img : tab.inactive_img
          }>
          </img>
          </div>
          </div>
        );
        })}
      </div>
    </div>
  );
};

export default TabOption
